import React from 'react';
import AccountsList from './accountsList';

class AccountsListContainer extends React.Component {
  constructor() {
    super()

    this.state = {
      books: []
    };
  }

  componentDidMount() {
    fetch('https://localhost:4000/accounts')
      .then(response => response.json())
      .then(bookData => this.setState({ books: bookData.books }))
  }

  render() {
    return <AccountsList accounts={this.state.accounts} />
  }
}


const mapStateToProps = state => {
    return {
      accounts: state.accounts,
      loading: state.loading_accounts
    }
  }

  const mapDispatchToProps = dispatch => {
    return {
      fetchAccounts: () => dispatch(fetchAccounts()),
      createAccount: (state) => dispatch(createAccount(state))
    }
  }
  export default connect(mapStateToProps, mapDispatchToProps)(AccountsList)
