 const accountTemplate = (state) => ({
 });

  export const fetchAccounts = () => {
    return (dispatch) => {
      dispatch({ type: 'LOADING_ACCOUNTS'})
      fetch('http://localhost:4000/accounts').then(response => {
        return response.json()
      }).then(responseJSON => {
        console.log(responseJSON)
        dispatch({ type: 'ADD_ACCOUNTS', accounts: responseJSON })
      })
    }
  }

  export const creatAccount = (state) => {
    let configurationObject = {
      method: "Post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify(_userObject(state)),
    };

    return (dispatch) => {
      dispatch({ type: 'CREATE_ACCOUNT'})
      fetch('http://localhost:4000/accounts').then(response => {
        return response.json()
      }).then(responseJSON => {
        console.log(responseJSON)
        dispatch({ type: 'ADD_ACCOUNTS', accounts: responseJSON })
      })
    }
  }

  export const createUser = (state) => {
    let configurationObject = {
      method: "Post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify(_userObject(state)),
    };
    return async (dispatch) => {
      const res = await fetch(BASEURL + "sign_up", configurationObject);
      const json = await res.json();
      if (json.error) {
        throw new Error(json.error + " " + json.message);
      }
      localStorage.setItem("token", json.jwt);
      dispatch({
        type: "CREATE_USER",
        payload: json.user,
      });
    };
  };
