import React, { Component } from 'react';
import { connect } from 'react-redux';

class CreateAccountForm extends Component {
  state = {
    text: ''
  };

  handleChange = event => {
    this.setState({
      text: event.target.value
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    //this.props.dispatch({ type: 'ADD_TODO', payload: this.state });
    console.log(this.state)
  };

  render() {
    let divStyle = {
      color: 'blue',
      backgroundColor: 'red'
    };

    return (
      <div  style={divStyle}>
        <ContentHeader title="Accounts"/>

<div  style={divStyle}>
  <h3>Create New Account:</h3>
  <form onSubmit={event => this.handleCreateAccountSubmit(event)}>
    <p>
      <label>Account Name:</label>
      <input
        type="text"
        onChange={event => this.handleChange(event)}
        placeholder={""}
      />
    </p>

    <p>
      <label>Cash:</label>
      <input
        type="text"
        onChange={event => this.handleCashChange(event)}
        value={this.props.lastName}
        placeholder={""}
      />
    </p>

    <p>
      <label>Bank Name:</label>
      <input
        type="text"
        onChange={event => this.handleBankNameChange(event)}
        value={this.props.bankName}
        placeholder={""}
      />
    </p>
    <input type="submit" />
  </form>
</div>
      </div>
    );
  }
}

export default connect()(CreateAccountForm);
