import React, { Component } from 'react';
import Account from './account'
import StoreContext from '../contexts/storeContext';
import { connect } from 'react-redux';
import { fetchAccounts, createAccount, createCounter } from '../actions/fetchAccounts'
import { fetchCats } from '../actions/fetchCats'
import  CreateAccountForm  from './createAccountForm'

class Home extends Component{

  static contextType = StoreContext;

  handleOnClick = () => {
    console.log("handleOnClicks")

    this.props.fetchAccounts()

    console.log(this.props)

}

  componentDidMount() {
    this.props.fetchAccounts()


  }

  handleCreateCounter = () => {
    this.props.createCounter()

  }

  handleCreateAccount = () => {
    this.props.createAccount({name: "new name"})
    this.props.fetchAccounts()
    this.render()

  }

  state = {
    name: ''
  };

  handleChange = event => {
    this.setState({
      name: event.target.value
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    //this.props.dispatch({ type: 'ADD_TODO', payload: this.state });
    console.log(this.state)
    this.props.createAccount({name: this.state.name})
    this.props.fetchAccounts()
    this.render()
  };

  render() {
    console.log("Props " ) // log will fire every time App renders
    console.log(this.props.accounts.accounts)

    let accounts = ""

    if(this.props.accounts != undefined){
      accounts = this.props.accounts.accounts
     // console.log(accounts[1])
      accounts = this.props.accounts.accounts.map(account => <div key={account.id}><Account name={account.name}/></div>);
    }
    else{
      accounts = <div>Loading ...</div>
    }

    let divStyle = {
      color: 'blue',
      backgroundColor: 'red'
    };

    return (

          <div className="Home">

          <div  style={divStyle}>
            <h3>Create New Account:</h3>
            <form onSubmit={event => this.handleSubmit(event)}>
              <p>
                <label>Account Name:</label>
                <input
                  type="text"
                  onChange={event => this.handleChange(event)}
                  placeholder={"name"}
                />
              </p>
              <input type="submit" />
            </form>
          </div>

          <br />

          <div  style={divStyle}>
            <h3>Create New Transaction:</h3>
            <form onSubmit={event => this.handleSubmit(event)}>
              <p>
                <label>Account Name:</label>
                <input
                  type="text"
                  onChange={event => this.handleChange(event)}
                  placeholder={"name"}
                />
              </p>
              <input type="submit" />
            </form>
          </div>



            <h2>Bank Accounts:</h2>


            {accounts}



          </div>
    );
  }
}


const mapStateToProps = state => {
  return {
    catPics: state.cats,
    accounts: state.accounts,
    loading: state.loading
  }
}

const mapDispatchToProps = dispatch => {
  return {
    fetchCats: () => dispatch(fetchCats()),
    fetchAccounts: () => dispatch(fetchAccounts()),
    createAccount: (state) => dispatch(createAccount(state)),
    createCounter: () => dispatch(createCounter()),
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Home)
